34 LRESULT MainWindow::HandleMessage(UINT uMsg, WPARAM wParam, LPARAM lParam)
35 {
36     switch (uMsg)
37     {
38         case WM_DESTROY:
39 
40             // Destroy 'WinMain()'? So be it...
41             PostQuitMessage(0);
42             return 0;
43 
44         case WM_PAINT:
45         {
46             PAINTSTRUCT ps;
47             HDC hdc = BeginPaint(m_hwnd, &ps);
48             FillRect(hdc, &ps.rcPaint, (HBRUSH) (COLOR_WINDOW+1));
49             EndPaint(m_hwnd, &ps);
50         }
51         return 0;
52 
53         default:
54             return DefWindowProc(m_hwnd, uMsg, wParam, lParam);
55     }
56     return TRUE;
57 }
